namespace M47.Shared.Tests.Utils.Text;

using M47.Shared.Utils.Text;

public class HtmlRemoveTagsTests
{
    [Theory]
    // No HTML tags
    [InlineData("< M47Labs >", "< M47Labs >")]
    [InlineData("<< M47Labs >>", "<< M47Labs >>")]
    [InlineData("< M47Labs>", "< M47Labs>")]
    [InlineData("<< M47Labs>>", "<< M47Labs>>")]

    // No HTML tags exceptions
    [InlineData("<M47Labs>", "")]
    [InlineData("<<M47Labs>>", "<>")]
    [InlineData("<M47Labs >", "")]
    [InlineData("<<M47Labs >>", "<>")]

    // Html tags
    [InlineData("Hi world!", "Hi world!")]
    [InlineData("Hi world!<a>", "Hi world!")]
    [InlineData("Hi <span> world!", "Hi  world!")]
    [InlineData("Hi </span> world!", "Hi  world!")]
    // HTML tags exceptions
    [InlineData("Hi < span > world!", "Hi < span > world!")]

    // Math expressions
    [InlineData("a <= b", "a <= b")]
    [InlineData("a > b", "a > b")]
    [InlineData("a <> b", "a <> b")]
    [InlineData("a <<- b", "a <<- b")]

    // real examples
    [InlineData("Una joven estadounidense ha sufrido graves secuelas derivadas del\ncoronavirus\n[/espana/comunidad-valenciana/abci-nueva-normativa-coronavirus-donde-obligatoria-mascarilla-y-casos-202203281018_noticia.html].\nLa modelo CLAIRE BRIDGES, de 21 años, ha padecido la amputación de\nsus dos piernas después de ser diagnosticada de miocarditis,\nrabdomiolisis, cianótica, acidosis y neumonía leve.\n\nDesde su contagio de coronavirus el pasado mes de enero la joven\nnatural de Florida LLEVA MESES «LUCHANDO POR SU VIDA». Aunque está\nvacunada, la grave afección cardiaca que tiene de nacimiento la\nconvierte en una persona de riesgo\n[/espana/madrid/abci-guia-para-actuar-nuevas-normas-frente-covid-202203291054_noticia.html]\ny provocó que su caso se agravara con el paso de los días.\n\nClaire Bridges necesitó respiración asistida desde su ingreso en la\nUCI y su situación empeoraría con el paso de los días. Su corazón\nllegó a pararse, aunque las maniobras de reanimación de los\nservicios médicos permitieron salvarle la vida. Nada se podía hacer,\nsin embargo, con sus extremidades inferiores.\n<blockquote class=\"instagram-media\" data-instgrm-captioned\ndata-instgrm-permalink=\"https://www.instagram.com/p/CbIzX0yptrM/?utm_source=ig_embed&amp;utm_campaign=loading\"\ndata-instgrm-version=\"14\" style=\" background:#FFF; border:0;\nborder-radius:3px; box-shadow:0 0 1px 0 rgba(0,0,0,0.5),0 1px 10px 0\nrgba(0,0,0,0.15); margin: 1px; max-width:540px; min-width:326px;\npadding:0; width:99.375%; width:-webkit-calc(100% - 2px);\nwidth:calc(100% - 2px);\"><div style=\"padding:16px;\"> <a\nhref=\"https://www.instagram.com/p/CbIzX0yptrM/?utm_source=ig_embed&amp;utm_campaign=loading\"\nstyle=\" background:#FFFFFF; line-height:0; padding:0 0;\ntext-align:center; text-decoration:none; width:100%;\" target=\"_blank\">\n<div style=\" display: flex; flex-direction: row; align-items:\ncenter;\"> <div style=\"background-color: #F4F4F4; border-radius: 50%;\nflex-grow: 0; height: 40